import { useState } from "react";
import { Button } from "@material-ui/core";
import { BsDot } from "react-icons/bs";
import Modal from "antd/lib/modal/Modal";
import { useDispatch, useSelector } from "react-redux";
import "./Flight.css";
import { Space, Table } from "antd";
import { Link, useHistory, useParams } from "react-router-dom";
const data = [
  {
    id: 1,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23:40",
    price: "1,280,000",
  },
  {
    id: 2,
    brand: "Bamboo",
    flight: "BB - 213",
    time: "21:35 - 23:40",
    price: "580,000",
  },
  {
    id: 3,
    brand: "Viet Jet",
    flight: "VJ - 413",
    time: "21:35 - 23:40",
    price: "980,000",
  },
  {
    id: 4,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23:40",
    price: "630,000",
  },
];

const columns = [
  {
    title: "Brand",
    dataIndex: "brand",
    filters: [
      {
        text: "Bamboo",
        value: "Bamboo",
      },
      {
        text: "VietJet",
        value: "VietJet",
      },
    ],
    onFilter: (value, record) => record.brand.indexOf(value) === 0,
  },

  {
    title: "Chuyến bay",
    dataIndex: "flight",
  },
  {
    title: "Thời gian",
    dataIndex: "time",
  },
  {
    title: "Giá",
    dataIndex: "price",
    sorter: (a, b) => a.price - b.price,
  },
  {
    title: "",
    key: "action",
    sorter: true,
    render: () => (
      <Space size="middle">
        <Button variant="contained" color="secondary" onClick={showModal}>
          Đặt vé
        </Button>
      </Space>
    ),
  },
];

function Flight() {
  const [visible, setVisible] = useState(false);
  const [top, setTop] = useState("none");
  const [bottom, setBottom] = useState("bottomRight");
  const [yScroll, setYScroll] = useState(false);
  const [xScroll, setXScroll] = useState(undefined);
  const [ellipsis, setEllipsis] = useState(false);
  const { id } = useParams();
  const users = useSelector((state) => state.infor.infor.data);
  const [state, setState] = useState({
    listdate: "",
    visible: false,
    visible2: false,
    visible3: false,
    name: "",
    email: "",
    sdt: "",
    diachi: "",
    nguoilon: 1,
    treem: 0,
    embe: 0,
    dieukhoan: false,
    valueDate: "",
    date: "",
    loadlaihoadon: 1,
  });

  const scroll = {};

  if (yScroll) {
    scroll.y = 240;
  }

  if (xScroll) {
    scroll.x = "100vw";
  }

  const tableColumns = columns.map((item) => ({ ...item, ellipsis }));
  const handleEllipsisChange = (enable) => {
    setEllipsis(enable);
  };
  const showModal = () => {
    setVisible(true);
  };

  const hideModal = () => {
    setVisible(false);
  };
  
  return (
    <>
      <Table
        // {...tableProps}
        pagination={{
          position: [top, bottom],
        }}
        columns={tableColumns}
        dataSource={data}
        scroll={scroll}
      />
      <Modal
        title="Đặt vé"
        visible={visible}
        onOk={hideModal}
        onCancel={hideModal}
        okText="ok"
        cancelText="Cancle"
      >
        <p>Bla bla ...</p>
        <p>Bla bla ...</p>
        <p>Bla bla ...</p>
      </Modal>
    </>
  );
}

export default Flight;
