import React, { useEffect } from "react";
import Select from 'react-select'
import { Link } from "react-router-dom";
import Footer from "../trangchu/footer/Footer";
import "./Ticket.css";

const destination = [
    {
        value: 'Hà Nội',
        label: 'Hà Nội'
    },
    {
        value: 'Đà Nẵng',
        label: 'Đà Nẵng'
    },
    {
        value: 'Điện Biên',
        label: 'Điện Biên'
    },
    {
        value: 'Cần Thơ',
        label: 'Cần Thơ'
    },
    {
        value: 'Hải Phòng',
        label: 'Hải Phòng'
    },
    {
        value: 'TP. Hồ Chí Minh',
        label: 'TP. Hồ Chí Minh'
    },

]
function Ticket() {
  useEffect(() => {
    window.scrollTo(0, 0);
  });
  return (
    <div id="ticket">
      <div className="breadcrumb">
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">
                <i className="fas fa-home mr-2"></i>Trang chủ /
              </Link>
            </li>
            <li className="breadcru mb-item"> Vé máy bay</li>
          </ol>
        </nav>
      </div>
      <div className="container">

        <div className="form-search">
            <div className='choose-destination'>
                <div className="choose">
                    <label>Chọn điểm đi</label>
                    <Select placeholder='Chọn điểm đi...' options={destination}/>
                </div>
                <div className="choose">
                    <label>Chọn điểm đến</label>
                    <Select placeholder='Chọn điểm đến...' options={destination}/>
                </div>
            </div>

        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Ticket;
