import { BsDot } from "react-icons/bs";
import "./Flight.css";
import { Switch, Table } from "antd";
import { useState } from "react";
const data = [
  {
    id: 1,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 2,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 3,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 4,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
];

const columns = [
  {
    title: "Brand",
    width: 100,
    dataIndex: "brand",
    key: "brand",
    fixed: "left",
  },
  {
    title: "Flight",
    width: 100,
    dataIndex: "flight",
    key: "flight",
    fixed: "left",
  },
  {
    title: "Time",
    dataIndex: "time",
    key: "1",
    width: 150,
  },
  {
    title: "Price",
    dataIndex: "price",
    key: "price",
    width: 150,
  },

  {
    title: "Action",
    key: "operation",
    fixed: "right",
    width: 100,
    render: () => <a>Chon</a>,
  },
];
function Flight() {
  const [fixedTop, setFixedTop] = useState(false);
  return (
    <Table
      columns={columns}
      dataSource={data}
      scroll={{
        x: 1500,
      }}
      summary={() => (
        <Table.Summary fixed={fixedTop ? "top" : "bottom"}>
          <Table.Summary.Row>
            <Table.Summary.Cell index={0} colSpan={2}>
              <Switch
                checkedChildren="Fixed Top"
                unCheckedChildren="Fixed Top"
                checked={fixedTop}
                onChange={() => {
                  setFixedTop(!fixedTop);
                }}
              />
            </Table.Summary.Cell>
            <Table.Summary.Cell index={2} colSpan={8}>
              Scroll Context
            </Table.Summary.Cell>
            <Table.Summary.Cell index={10}>Fix Right</Table.Summary.Cell>
          </Table.Summary.Row>
        </Table.Summary>
      )}
      sticky
    />
  );
}

export default Flight;
