import { useState } from "react";
import { Button } from "@material-ui/core";
import { BsDot } from "react-icons/bs";
import "./Flight.css";
import { Space, Table } from "antd";

const data = [
  {
    id: 1,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 2,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 3,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
  {
    id: 4,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23 - 40",
    price: "780,000",
  },
];

const columns = [
  {
    title: "Brand",
    dataIndex: "brand",
    filters: [
      {
        text: "Bamboo",
        value: "Bamboo",
      },
      {
        text: "VietJet",
        value: "VietJet",
      },
    ],
    onFilter: (value, record) => record.address.indexOf(value) === 0,
  },

  {
    title: "Flight",
    dataIndex: "flight",
  },
  {
    title: "Time",
    dataIndex: "time",
  },
  {
    title: "Price",
    dataIndex: "price",
    sorter: (a, b) => a.price - b.price,
  },
  {
    title: "Action",
    key: "action",
    sorter: true,
    render: () => (
      <Space size="middle">
        <Button>Select</Button>
      </Space>
    ),
  },
];
function Flight() {
  const [bottom, setBottom] = useState('bottomRight');
  const [yScroll, setYScroll] = useState(false);
  const [xScroll, setXScroll] = useState(undefined);
  
  const scroll = {};

  if (yScroll) {
    scroll.y = 240;
  }

  if (xScroll) {
    scroll.x = '100vw';
  }

  const tableColumns = columns.map((item) => ({ ...item, ellipsis }));


  return (
    <Table
      // {...tableProps}
      pagination={{
        position: [top, bottom],
      }}
      columns={tableColumns}
      dataSource={data}
      scroll={scroll}
    />
  );
}

export default Flight;
