import { useState } from "react";
import { Button } from "@material-ui/core";
import { BsDot } from "react-icons/bs";
import Modal from "antd/lib/modal/Modal";
import { useDispatch, useSelector, } from "react-redux";
import "./Flight.css";
import taikhoanApi from "../../../../api/taikhoanApi";
import { Space, Table, Carousel, message, Popover, Radio, Rate, Spin } from "antd";
import { Link, useHistory, useParams } from "react-router-dom";
const data = [
  {
    id: 1,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23:40",
    price: "1,280,000",
  },
  {
    id: 2,
    brand: "Bamboo",
    flight: "BB - 213",
    time: "21:35 - 23:40",
    price: "580,000",
  },
  {
    id: 3,
    brand: "Viet Jet",
    flight: "VJ - 413",
    time: "21:35 - 23:40",
    price: "980,000",
  },
  {
    id: 4,
    brand: "VietNam Airline",
    flight: "VN - 213",
    time: "21:35 - 23:40",
    price: "630,000",
  },
];

const columns = [
  {
    title: "Brand",
    dataIndex: "brand",
    filters: [
      {
        text: "Bamboo",
        value: "Bamboo",
      },
      {
        text: "VietJet",
        value: "VietJet",
      },
    ],
    onFilter: (value, record) => record.brand.indexOf(value) === 0,
  },

  {
    title: "Flight",
    dataIndex: "flight",
  },
  {
    title: "Time",
    dataIndex: "time",
  },
  {
    title: "Price",
    dataIndex: "price",
    sorter: (a, b) => a.price - b.price,
  },
  {
    title: "Action",
    key: "action",
    sorter: true,
    render: () => (
      <Space size="middle">
        <Button
          className="float-right position-absolute btn-dt"
          onClick={showModal}
          variant="contained"
          color="secondary"
        >
          Select
        </Button>
      </Space>
    ),
  },
];
function Flight() {
  const [top, setTop] = useState("none");
  const [bottom, setBottom] = useState("bottomRight");
  const [yScroll, setYScroll] = useState(false);
  const [xScroll, setXScroll] = useState(undefined);
  const [ellipsis, setEllipsis] = useState(false);
  const { id } = useParams();
  const users = useSelector((state) => state.infor.infor.data);
  const [state, setState] = useState({
    listdate: "",
    visible: false,
    visible2: false,
    visible3: false,
    name: "",
    email: "",
    sdt: "",
    diachi: "",
    nguoilon: 1,
    treem: 0,
    embe: 0,
    dieukhoan: false,
    valueDate: "",
    date: "",
    loadlaihoadon: 1,
  });

  const showModal = async () => {
    if (users) {
      var user = await taikhoanApi.getOne(+users.id).then((data) => {
        return data;
      });
      setState({
        ...state,
        visible3: false,
        visible: true,
        name: user.name,
        diachi: user.diachi,
        sdt: user.sdt,
        email: user.email,
      });
    } else {
      message.warning("Bạn cần đăng nhập trước!");
    }
  };
 
  const scroll = {};

  if (yScroll) {
    scroll.y = 240;
  }

  if (xScroll) {
    scroll.x = "100vw";
  }

  const tableColumns = columns.map((item) => ({ ...item, ellipsis }));
  const handleEllipsisChange = (enable) => {
    setEllipsis(enable);
  };

  return (
    <>
    <Table
      // {...tableProps}
      pagination={{
        position: [top, bottom],
      }}
      columns={tableColumns}
      dataSource={data}
      scroll={scroll}
    />
    <Modal
        title="Đặt vé"
        visible={state.visible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <h4 className="text-center text-primary">Thông tin liên lạc</h4>
        <div className="form-group">
          <label htmlFor="">Họ tên(*)</label>
          <input
            type="text"
            className="form-control"
            name="name"
            disabled
            value={name}
            onChange={onchange}
            aria-describedby="helpId"
            placeholder=""
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Email(*)</label>
          <input
            type="email"
            className="form-control"
            name="email"
            disabled
            value={email}
            onChange={onchange}
            aria-describedby="helpId"
            placeholder=""
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Số điện thoại(*)</label>
          <input
            type="text"
            className="form-control"
            name="sdt"
            disabled
            value={sdt}
            onChange={onchange}
            aria-describedby="helpId"
            placeholder=""
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Địa chỉ</label>
          <input
            type="text"
            className="form-control"
            name="diachi"
            disabled
            value={diachi}
            onChange={onchange}
            aria-describedby="helpId"
            placeholder=""
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Ngày đi</label>
          <input
            type="text"
            className="form-control"
            name="diachi"
            disabled
            value={
              state.date === "" ? formatlaidate(checkngaydi()) : state.date
            }
            onChange={onchange}
            aria-describedby="helpId"
            placeholder=""
          />
        </div>
        <h4 className="text-center text-primary">Số người</h4>
        <div className="row">
          <div className="col-md-3">
            <div className="form-group">
              <label htmlFor="">Người lớn</label>
              <input
                type="number"
                className="form-control"
                name="nguoilon"
                min="1"
                value={nguoilon}
                onChange={onchange}
                aria-describedby="helpId"
                placeholder=""
              />
            </div>
          </div>
          <div className="col-md-3">
            <div className="form-group">
              <label htmlFor="">Trẻ em</label>
              <input
                type="number"
                className="form-control"
                name="treem"
                min="0"
                value={treem}
                onChange={onchange}
                aria-describedby="helpId"
                placeholder=""
              />
            </div>
          </div>
          <div className="col-md-3">
            <div className="form-group">
              <label htmlFor="">Em bé</label>
              <input
                type="number"
                className="form-control"
                value={embe}
                name="embe"
                min="0"
                onChange={onchange}
                aria-describedby="helpId"
                placeholder=""
              />
            </div>
          </div>
          <div className="col-md-3">
            <div className="form-group">
              <label htmlFor="">Tổng</label>
              <input
                type="number"
                disabled
                className="form-control"
                name="tong"
                value={tong}
                aria-describedby="helpId"
                placeholder=""
              />
            </div>
          </div>
        </div>
        <h4 className="text-center text-primary">Thành tiền</h4>
        {tour_ngay.map((ok) => (
          <p key={ok.id}>
            Số tiền cần phải trả:{" "}
            <strong className="text-danger">
              {thanhtien(ok.giatreem, ok.giaembe).toLocaleString()}
            </strong>
          </p>
        ))}
      </Modal>
      <Modal
        title="Đặt tour"
        visible={state.visible2}
        onOk={handleOk2}
        onCancel={handleCancel2}
      >
        <h4 className="text-center text-primary">Hình thức thanh toán</h4>
        <Hinhthucthanhtoan callback={callbackfunction} />
        <h4 className="text-center text-primary">Điều khoản</h4>
        <div className="dieukhoan">
          <Dieukhoan />
        </div>
        <input
          type="checkbox"
          onChange={onchange}
          className="mt-3"
          name="dieukhoan"
          id="dk"
        />
        <label htmlFor="dk" className="ml-3">
          <strong>Tôi đồng ý với điều khoản ở trên</strong>
        </label>
      </Modal>
    </>
  );
}

export default Flight;
